mapper.insert:
INSERT INTO letcrud(
	CRUD_ID
	, CRUD_SJ
	, CRUD_CN
	, USER_NM
	, FRST_REGIST_PNTTM
	) VALUES (
		#{crudId}
		, #{crudSj}
		, #{crudCn}
		, #{userNm}
		, (SELECT DATE_FORMAT(SYSDATE(),'%Y-%m-%d'))
	)

mapper.update:
UPDATE letcrud SET
 CRUD_SJ = #{crudSj}
 , USER_NM = #{userNm}
 , CRUD_CN = #{crudCn}
WHERE CRUD_ID = #{crudId}
